css:
.rest-client {
  position: relative;
  overflow: auto;
}
.rest-client .btn {
  margin: 5px 0;
}
.rest-client .buffer {
  margin-left: 2px;
  margin-right: 2px;
}
.rest-client-url-container {
  float: left;
  width: 50%;
}
@media only screen and (max-width: 800px) {
  .rest-client-url-container {
    width: 100%;
    float: none;
  }
}
.rest-client-result-container {
  float: right;
  width: 50%;
}
.rest-client-result-container .rest-client-open-in-editor {
  float: right;
}
@media only screen and (min-width: 800px) {
  .rest-client-result-container {
    padding-top: 0;
  }
}
@media only screen and (max-width: 800px) {
  .rest-client-result-container {
    width: 100%;
    float: none;
    padding-left: 0;
    padding-right: 0;
  }
}
.rest-client-url {
  width: 100%;
  font-size: 15px;
  font-family: Consolas, 'Courier New';
}
.rest-client-headers,
.rest-client-payload {
  width: 100%;
  font-family: Consolas, 'Courier New';
}
.rest-client-action-btns {
  display: block;
  float: right;
  text-align: right;
}
.rest-client-action-btns .rest-client-clear {
  margin-right: 10px;
  color: #cc0000;
}
.rest-client-action-btns .rest-client-clear:hover {
  color: #ff0000;
}
.rest-client-action-btns .rest-client-send {
  color: #2bda77;
}
.rest-client-action-btns .rest-client-send:hover {
  color: #56e293;
}
.field {
  color: #aaaaaa;
  background-color: #212224;
  border: 1px solid rgba(0, 0, 0, 0.5);
}
.field.rest-client-user-agent,
.field.rest-client-proxy-server,
.field.rest-client-strict-ssl {
  padding: 0 5px;
  margin: 10px 5px;
}
.float-right {
  float: right;
}
.lnk {
  text-decoration: none;
}
.lnk:hover {
  text-decoration: underline;
  cursor: pointer;
}
input[type='text'],
textarea {
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  resize: none;
}
.rest-client-request-link-remove {
  padding-left: 10px;
}
.rest-client-header {
  color: #5293d8;
}
.rest-client-header:hover {
  cursor: pointer;
}
.header-expanded::before {
  content: '↓ ';
}
.header-collapsed::before {
  content: '→ ';
}
.header-collapsed::after {
  content: ' …';
}
